import { useEffect, useState } from "react"
import Point from "../Point";
import "./grid.css"
// use  context provider to determine what ill be clicking?
const Grid = (props) =>{
    let rows = 10 //30
    let columns = 10 //62
    const [grid,setGrid] = useState([]);
    const [isMouseDown,setIsMouseDown] = useState(false)
    let currPointer = props.currPointer;
    console.log(currPointer)
    useEffect( () => {
        //when trying to map over the 2d array if the values are empty it doesn actually do anything but itll if I add things
        let newGrid = []
        // for (let i  = 0 ;  i < 10;i++){
        //     newGrid[i] = new Array(10)
        //     newGrid[i][0] = 1
        //     newGrid[i][1] = 2
        //     newGrid[i][2] = 3
        //     newGrid[i][3] = 4
        //     newGrid[i][4] = 5
        //     newGrid[i][5] = 6
        //     newGrid[i][6] = 7
        //     newGrid[i][7] = 8
        //     newGrid[i][8] = 9
        //     newGrid[i][9] = 10
        // }
        for(let i = 0 ; i < rows ; i++){
            let currRow = [];
            for(let j = 0 ; j < columns ; j++){
                currRow.push({
                    row:i,
                    column:j,
                    type:null
                })
            }
            newGrid.push(currRow)
        }
        setGrid(newGrid)

    },[])
    function mouseDown(row,col){
        let newGrid = handleChange(row,col,grid);
        setGrid(newGrid)
        setIsMouseDown(true)
        return currPointer
    }
    function mouseEnter(row,col){
        if (isMouseDown){
        let newGrid = handleChange(row,col,grid)
        setGrid(newGrid)
        return currPointer
        }
    }
    function mouseUp(row,col){
        setIsMouseDown(false)
    }

    function handleChange(row,col){
        let newGrid = grid;
        if(newGrid[row][col].type === null || newGrid[row][col].type !== currPointer){
            console.log(`Point is now a ${currPointer}`)
            newGrid[row][col].type = currPointer
        }
         else if(newGrid[row][col].type === currPointer){
            console.log(`Point is now null`)
            newGrid[row][col].type = null
         }
         return newGrid
    }
    return <div className="grid">
    {grid.map((currRow,currRowIndex) => {
        // return <Point x={1}></Point>
        return <div className="column" key={currRowIndex}>
        {currRow.map( (currColumn,currColumnIndex) => <Point
         x={currRowIndex}
         y={currColumnIndex}
         type={grid[currRowIndex][currColumnIndex].type}
         rows={rows}
         columns={columns}
         currPointer={currPointer}
         mouseDown={mouseDown}
         mouseEnter={mouseEnter}
         mouseUp={mouseUp}
         onMouseDown={() => {props.mouseDown(currRowIndex,currColumnIndex)
         }}
         onMouseEnter={(e)=> {props.mouseEnter(currRowIndex,currColumnIndex)}}
         onMouseUp={(e)=>{props.mouseUp(currRowIndex,currColumnIndex)
         }}
         > </Point>)}
        </div>
    })}
     </div>
}

export default Grid
