import { useEffect, useState } from "react"
import Point from "../Point";
// use  context provider to determine what ill be clicking?
const Grid = () =>{
    const [grid,setGrid] = useState([]);

    useEffect( () => {
        let newGrid = new Array(10);
        for (let i  = 0 ;  i < 10;i++){
            newGrid[i] = new Array(10)
        }
        setGrid(newGrid)
    },[])
    return <div className="grid">
    {grid.map((currRow,currRowIndex) => {

       return currRow.map( (currColumn,currColumnIndex) =>{
        return <Point x={currRowIndex} y={currColumnIndex}></Point>
        })
    })}
     </div>
}

export default Grid
